mapper.select:
SELECT TC_SEQ, COMP_NAME, IN_TIME, TAG_ID, CAR_TAG_ID FROM PG_RFID_LOG PRL INNER JOIN PG_TEST_SCHDULE PTS ON PRL.TC_RESERV_CODE = PTS.TC_RESERV_CODE WHERE 1=1 AND (OUT_TIME IS NULL OR OUT_TIME='') AND PRL.TC_DAY=#{tcDay}
AND T_ID IN ('T003', 'T010')
AND T_ID IN ('T004', 'T005')
AND T_ID IN ('T007', 'T011')
AND T_ID IN ('T008', 'T009')
AND T_ID = #{tId}

SELECT PTS.TC_SEQ, PTS.TC_RESERV_CODE, IN_TIME, OUT_TIME, COMP_NAME, PT.T_NICKNAME, TAG_ID, CAR_TAG_ID, D_NAME, RM_LEVEL AS D_LEVEL, RM_W_CH AS WCH FROM PG_RFID_LOG PRL INNER JOIN PG_TRACK PT ON PRL.T_ID = PT.T_ID INNER JOIN PG_TEST_SCHDULE PTS ON PTS.TC_RESERV_CODE = PRL.TC_RESERV_CODE INNER JOIN PG_RESOURCE_MAPPING PRM ON PTS.TC_SEQ = PRM.TC_SEQ WHERE
PRM.D_SEQ = #{dSeq}
PRM.D_NAME = #{dName}
AND PRM.TC_DAY=#{tcDay} ORDER BY RL_SEQ DESC LIMIT 1

SELECT DISTINCT PHONE FROM (
	SELECT MEM_PHONE AS PHONE FROM PG_MEMBER PM WHERE KAKAO_SMS_YN='Y'
	UNION ALL 
	SELECT D_PHONE AS PHONE FROM PG_DRIVER PD INNER JOIN PG_RESOURCE_MAPPING PRM
	ON PD.D_SEQ = PRM.D_SEQ WHERE TC_DAY=#{tcDay}
) A
