mapper.select:
SELECT D_SEQ, D_NAME, D_LICENSE_TYPE, D_EDU, D_EDU_DT, D_EDU_END_DT, D_REG_DT, D_REG_USER, D_MOD_DT, D_MOD_USER, pd.COMP_CODE, pc.COMP_NAME, pc.COMP_LICENSE, pc.BLACK_LIST, D_BIRTH, D_EMAIL, D_PHONE, D_PHONE2, D_HISTORY, D_BLOOD_TYPE, D_BLOOD_SPECIAL, D_LEVEL, D_APPROVAL, D_MEMO FROM PG_DRIVER pd INNER JOIN PG_COMPANY pc ON pd.COMP_CODE=pc.COMP_CODE WHERE 1=1
AND ( COMP_NAME LIKE CONCAT('%',#{keyword},'%') OR COMP_LICENSE LIKE CONCAT('%',#{keyword},'%') OR D_NAME = #{enckeyword} )
AND D_NAME LIKE CONCAT('%',#{dName},'%')
AND pd.COMP_CODE = #{compCode}
AND D_APPROVAL IN ('N', 'R')
AND D_REG_DT BETWEEN #{stDt} AND #{edDt}
AND D_APPROVAL = #{dApproval}
AND D_MOD_DT BETWEEN #{stDt} AND #{edDt}
ORDER BY D_SEQ DESC LIMIT #{startRowNum} ,#{pageSize}

         SELECT COUNT(*)
         FROM PG_DRIVER pd INNER JOIN PG_COMPANY pc ON pd.COMP_CODE=pc.COMP_CODE WHERE 1=1

         	AND (
         		COMP_NAME LIKE CONCAT('%',#{keyword},'%') OR
         		COMP_LICENSE LIKE CONCAT('%',#{keyword},'%') OR
         		D_NAME = #{enckeyword}
         	)


         	AND D_NAME LIKE CONCAT('%',#{dName},'%')


            AND pd.COMP_CODE = #{compCode}



	            AND D_APPROVAL IN ('N', 'R')

	         		AND D_REG_DT BETWEEN #{stDt} AND #{edDt}



            	AND D_APPROVAL = #{dApproval}



	         		AND D_MOD_DT BETWEEN #{stDt} AND #{edDt}

mapper.update:
        UPDATE PG_DRIVER SET
        	D_APPROVAL='R',
        	D_MEMO=#{dMemo},
        	D_MOD_DT=#{dModDt},
        	D_MOD_USER=#{dModUser}
		WHERE D_SEQ=#{dSeq}

UPDATE FILE_MANAGEMENT
SET F_NAME = #{fName},
F_URL = #{fUrl}
WHERE F_GROUP = #{fGroup}

mapper.select:
        SELECT F_SEQ, F_TYPE, F_NAME, F_URL FROM FILE_MANAGEMENT
		WHERE F_GROUP=#{fGroup}

            AND F_TYPE=#{fType}


         	AND F_TYPE IN ('a', 'd', 'e')

mapper.delete:
        DELETE FROM FILE_MANAGEMENT
		WHERE F_GROUP=#{fGroup}

            AND F_TYPE=#{fType}


         	AND F_TYPE IN ('a', 'd', 'e')

mapper.update:
        UPDATE FILE_MANAGEMENT
		SET F_NAME=#{fName}, F_URL=#{fUrl}, F_REG_DT=#{fRegDt}
		WHERE F_SEQ=${fSeq}
