mapper.update:
UPDATE PG_RESOURCE_MAPPING
SET R_ID = #{rId},
RM_W_CH = #{rmWCh},
W_ID = #{wId}
WHERE D_SEQ = #{dSeq}
AND TC_SEQ = #{tcSeq}

UPDATE PG_RESOURCE_MAPPING
SET R_ID = #{rId}
WHERE D_SEQ = #{dSeq}

UPDATE PG_RESOURCE_MAPPING
SET

  R_ID = #{rId}


  W_ID = #{wId},
  RM_W_CH = #{wCh}

WHERE D_SEQ = #{dSeq}
AND TC_SEQ = #{tcSeq}

UPDATE PG_HINT_RESOURCE
SET

  R_ID = #{rId},
  R_RETURN = #{rReturn}


  W_ID = #{wId},
  W_CH = #{wCh},
  W_RETURN = #{wReturn}

WHERE HR_SEQ = #{hrSeq}

UPDATE PG_RESOURCE_MAPPING
SET

  R_ID = #{rId}


  W_ID = #{wId}

WHERE D_SEQ = #{employeeNo}
AND TC_DAY >= DATE_FORMAT(SYSDATE(), '%Y%m%d');

UPDATE PG_RESOURCE_MAPPING
SET

  R_ID = null


  W_ID = null

WHERE D_SEQ = #{employeeNo}
AND TC_DAY >= DATE_FORMAT(SYSDATE(), '%Y%m%d');

UPDATE PG_RESOURCE_MAPPING
SET
  R_ID = null
WHERE C_CODE = #{vhclCode}
AND TC_DAY >= DATE_FORMAT(SYSDATE(), '%Y%m%d');

mapper.select:
SELECT PRM.RM_SEQ, PHD.NAME, PHD.EMPLOYEE_NO, PHD.CRN_DTM, MAX(PRM.TC_DAY) as TC_DAY, PHR.R_RETURN FROM PG_RESOURCE_MAPPING PRM LEFT OUTER JOIN PG_HINT_DRIVER PHD on PRM.D_SEQ = PHD.EMPLOYEE_NO LEFT OUTER JOIN PG_HINT_RESOURCE PHR on PHD.EMPLOYEE_NO = PHR.EMPLOYEE_NO WHERE PRM.RM_IN_OUT = 'I' AND PRM.RM_TYPE = 'D' AND PRM.N_DCCP_YN = 'N' AND PRM.TC_DAY < DATE_FORMAT(SYSDATE(),'%Y%m%d')
AND ( PHD.NAME LIKE CONCAT('%',#{text},'%') OR PHD.EMPLOYEE_NO LIKE CONCAT('%',#{text},'%') )
AND PRM.TC_DAY BETWEEN #{stDate} AND #{edDate}

mapper.update:
UPDATE PG_RFID
SET R_IN_OUT = #{inOut}
WHERE R_ID = #{rId}

UPDATE PG_WIRESS
SET W_IN_OUT = #{inOut}
WHERE W_ID = #{wId}
