mapper.select:
SELECT pts.TM_SEQ, ptt.PLN_DTM , ptt.REQ_NO, ptt.TEST_ITEM_NAME, ptt.SET_SIZE, ptt.TIRE_SIZE, ptt.WHEEL_SIZE, ptt.VHCL_CODE, ptt.VHCL_NAME, ptt.RETURN_SCRAP, pts.TM_ENGINEER as ENGINEER, pts.TIRE_PUSH, pts.TM_LIFT as LIFT, location.LOCAKY as TIRE_NUMBER, location.WAREKY as TIRE_LOCATION, pts.WHEEL_LOCATION, pts.TM_ASSEMBLY, pts.TM_DISASSY, pts.TM_TRWH_YN FROM PG_TIRE_TOTAL ptt JOIN PG_TIRE_MANAGEMENT pts ON ptt.REQ_NO=pts.TC_REQUEST_NUMBER and ptt.PLN_DTM = pts.TC_DAY and ptt.TEST_ITEM_NAME = pts.TEST_ITEM_NAME LEFT JOIN (select REQ_NO,TC_DAY,TEST_ITEM_NAME, group_concat(distinct WAREKY) as WAREKY , group_concat(distinct LOCAKY) as LOCAKY FROM PG_TIRE JOIN IFSTK_TIRE ON PG_TIRE.TIRE_BARCODE_NO = IFSTK_TIRE.LOTA01 GROUP BY REQ_NO,TC_DAY,TEST_ITEM_NAME) location ON location.REQ_NO = ptt.REQ_NO AND location.TC_DAY = ptt.PLN_DTM AND location.TEST_ITEM_NAME = ptt.TEST_ITEM_NAME
WHERE ptt.PLN_DTM between #{date} and #{endDate} and ptt.PLN_DTM != #{today}
where ptt.PLN_DTM = #{today}

SELECT pts.TM_SEQ, ptt.PLN_DTM , ptt.REQ_NO, ptt.TEST_ITEM_NAME, ptt.SET_SIZE, ptt.TIRE_SIZE, ptt.WHEEL_SIZE, ptt.VHCL_CODE, ptt.VHCL_NAME, ptt.RETURN_SCRAP, pts.TM_ENGINEER as ENGINEER, pts.TIRE_PUSH, pts.TM_LIFT as LIFT, location.LOCAKY as TIRE_NUMBER, location.WAREKY as TIRE_LOCATION, pts.WHEEL_LOCATION, pts.TM_ASSEMBLY, pts.TM_DISASSY, pts.TM_TRWH_YN FROM PG_TIRE_TOTAL ptt JOIN PG_TIRE_MANAGEMENT pts ON ptt.REQ_NO=pts.TC_REQUEST_NUMBER and ptt.PLN_DTM = pts.TC_DAY and ptt.TEST_ITEM_NAME = pts.TEST_ITEM_NAME LEFT JOIN (select REQ_NO,TC_DAY,TEST_ITEM_NAME, group_concat(distinct WAREKY) as WAREKY , group_concat(distinct LOCAKY) as LOCAKY FROM PG_TIRE JOIN IFSTK_TIRE ON PG_TIRE.TIRE_BARCODE_NO = IFSTK_TIRE.LOTA01 GROUP BY REQ_NO,TC_DAY,TEST_ITEM_NAME) location ON location.REQ_NO = ptt.REQ_NO AND location.TC_DAY = ptt.PLN_DTM AND location.TEST_ITEM_NAME = ptt.TEST_ITEM_NAME
WHERE ptt.PLN_DTM between #{date} and #{endDate} and ptt.PLN_DTM != #{today}
where ptt.PLN_DTM = #{today}
