mapper.select:
SELECT * FROM COM_CODE WHERE 1=1
AND C_PARENT = #{cParent}
ORDER BY C_ORDER ASC

mapper.insert:
INSERT INTO COM_CODE(
C_PARENT
, C_ID
, C_NAME
, C_VALUE
, C_TYPE
, C_ORDER
)
VALUES (
#{cParent}
, #{cId}
, ${cName}
, #{cValue}
, #{cType}
, #{cOrder}
)

mapper.update:
UPDATE COM_CODE
SET C_PARENT = #{cParent}
, C_ID = #{cId}
, C_NAME = #{cParent}
, C_VALUE = #{cValue}
, C_TYPE = #{cType}
, C_ORDER = #{cOrder}
WHERE C_SEQ = #{cSeq}
