mapper.select:
SELECT PSS.WSS_RESERV_DAY, PSS.WSS_ST_DAY, PSS.WSS_ED_DAY, PSS.WSS_APPROVAL, PSS.WS_CODE, PS.WS_NAME, PC.COMP_NAME FROM PG_SHOP_SCHDULE PSS INNER JOIN PG_SHOP PS on PSS.WS_CODE = PS.WS_CODE INNER JOIN PG_COMPANY PC on PSS.COMP_CODE = PC.COMP_CODE WHERE 1=1 AND PSS.WSS_APPROVAL != 'R' AND PSS.WSS_APPROVAL != 'C'
AND PSS.WSS_APPROVAL = #{wssApproval}
AND PSS.WSS_ST_DAY >= #{wssStDay}
AND PSS.WS_CODE = #{wsCode}
ORDER BY WSS_ST_DAY,WSS_ED_DAY

mapper.insert:
INSERT INTO PG_SHOP_SCHDULE(
  COMP_CODE
  , WSS_ST_DAY
  , WSS_ED_DAY
  , WSS_RESERV_DAY
  , WSS_RESERV_NAME
  , WSS_APPROVAL
  , WSS_MEMO
  , WSS_RESERV_CODE
  , WS_CODE
  , WS_PRICE
  , WSS_REG_USER
  , WSS_REG_DT
  )
  VALUES(
  #{compCode}
  ,#{wssStDay}
  ,#{wssEdDay}
  ,#{wssReservDay}
  ,#{wssReservName}
  ,#{wssApproval}
  ,#{wssMemo}
  ,#{wssReservCode}
  ,#{wsCode}
  ,#{wsPrice}
  ,#{wssRegUser}
  ,DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s')
  )

mapper.update:
UPDATE PG_SHOP_SCHDULE
  SET COMP_CODE = #{compCode}
  , WSS_ST_DAY = #{wssStDay}
  , WSS_ED_DAY = #{wssEdDay}
  , WSS_RESERV_NAME = #{wssReservName}
  , WSS_APPROVAL = #{wssApproval}
  , WSS_MEMO = #{wssMemo}
  , WS_CODE = #{wsCode}
  , WS_PRICE = #{wsPrice}
  , WSS_REG_USER = #{wssRegUser}
  , WSS_REG_DT = DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s')
  WHERE WSS_RESERV_CODE = #{wssReservCode}

mapper.select:
SELECT WS_PRICE
FROM PG_SHOP
WHERE WS_CODE = #{wsCode}

mapper.update:
UPDATE PG_MEMBER
SET MEM_PHONE = #{memPhone},
MEM_EMAIL = #{memEmail}
WHERE MEM_ID = #{memId}

mapper.select:
SELECT COUNT(*)
FROM PG_SHOP_SCHDULE
WHERE WS_CODE = #{wsCode}
AND WSS_ST_DAY  <=  #{wssEdDay}
AND WSS_ED_DAY  >=  #{wssStDay}

SELECT COUNT(*)
FROM PG_WEEK_DAY
WHERE WD_KIND = #{wsCode}
AND WD_ST_DT  <=  #{wssEdDay}
AND WD_ED_DT  >=  #{wssStDay}
AND WD_CON = 1

SELECT COUNT(*)
FROM PG_DAY_OFF
WHERE 1=1
AND DO_KIND = 'F'
AND DO_ST_DAY  <=  #{wssEdDay}
AND DO_ED_DAY  >=  #{wssStDay}
